package com.sos.JSHelper.Exceptions;

public class MissingWritePermissionException extends JobSchedulerException {

    /**
	 * 
	 */
    private static final long serialVersionUID = 1L;

    public MissingWritePermissionException() {
        this("Don't have write permissions to a file!");
    }

    public MissingWritePermissionException(String message) {
        super(message);
        this.Status(JobSchedulerException.PENDING);
        this.Category(CategoryFileHandling);

    }

}
